SELECT 年龄组, SUM(CASE WHEN 性别 = 'Male' THEN 1 ELSE 0 END) AS male_count, SUM(CASE WHEN 性别 = 'Female' THEN 1 ELSE 0 END) AS female_count, SUM(CASE WHEN 婚姻状况 = 'Married' THEN 1 ELSE 0 END) AS married_count, SUM(CASE WHEN 婚姻状况 = 'Single' THEN 1 ELSE 0 END) AS single_count, SUM(CASE WHEN 教育程度 = 1 THEN 1 ELSE 0 END) AS edu_1, SUM(CASE WHEN 教育程度 = 2 THEN 1 ELSE 0 END) AS edu_2, SUM(CASE WHEN 教育程度 = 3 THEN 1 ELSE 0 END) AS edu_3, SUM(CASE WHEN 教育程度 = 4 THEN 1 ELSE 0 END) AS edu_4, SUM(CASE WHEN 教育程度 = 5 THEN 1 ELSE 0 END) AS edu_5, MAX(通勤距离) AS max_commute, MIN(通勤距离) AS min_commute, AVG(总工作年限) AS avg_total_years, AVG(在本公司工作年限) AS avg_company_years FROM employees WHERE 在本公司工作年限 >= 2 GROUP BY 年龄组 ORDER BY 年龄组 ASC;

SELECT 年龄组, COUNT(CASE WHEN 性别 = 'Male' THEN 1 END) AS male_count, COUNT(CASE WHEN 性别 = 'Female' THEN 1 END) AS female_count, COUNT(CASE WHEN 婚姻状况 = 'Married' THEN 1 END) AS married_count, COUNT(CASE WHEN 婚姻状况 = 'Single' THEN 1 END) AS single_count, COUNT(CASE WHEN 教育程度 = 1 THEN 1 END) AS edu_1, COUNT(CASE WHEN 教育程度 = 2 THEN 1 END) AS edu_2, COUNT(CASE WHEN 教育程度 = 3 THEN 1 END) AS edu_3, COUNT(CASE WHEN 教育程度 = 4 THEN 1 END) AS edu_4, COUNT(CASE WHEN 教育程度 = 5 THEN 1 END) AS edu_5, MAX(通勤距离) AS max_commute, MIN(通勤距离) AS min_commute, AVG(总工作年限) AS avg_total_years, AVG(在本公司工作年限) AS avg_company_years FROM employees WHERE 在本公司工作年限 >= 2 GROUP BY 年龄组 ORDER BY 年龄组 ASC;

SELECT 年龄组, SUM(CASE WHEN 性别 = 'Male' THEN 1 ELSE 0 END) AS 男员工数量, SUM(CASE WHEN 性别 = 'Female' THEN 1 ELSE 0 END) AS 女员工数量, SUM(CASE WHEN 婚姻状况 = 'Married' THEN 1 ELSE 0 END) AS 结婚员工数量, SUM(CASE WHEN 婚姻状况 = 'Single' THEN 1 ELSE 0 END) AS 未婚员工数量, SUM(CASE WHEN 教育程度 = 1 THEN 1 ELSE 0 END) AS 教育程度1人数, SUM(CASE WHEN 教育程度 = 2 THEN 1 ELSE 0 END) AS 教育程度2人数, SUM(CASE WHEN 教育程度 = 3 THEN 1 ELSE 0 END) AS 教育程度3人数, SUM(CASE WHEN 教育程度 = 4 THEN 1 ELSE 0 END) AS 教育程度4人数, SUM(CASE WHEN 教育程度 = 5 THEN 1 ELSE 0 END) AS 教育程度5人数, MAX(通勤距离) AS 最远通勤距离, MIN(通勤距离) AS 最近通勤距离, AVG(总工作年限) AS 平均总工作年限, AVG(在本公司工作年限) AS 平均本公司工作年限 FROM employees WHERE 在本公司工作年限 >= 2 GROUP BY 年龄组 ORDER BY 年龄组 ASC;

SELECT `年龄组`, SUM(CASE WHEN `性别` = 'Male' THEN 1 ELSE 0 END) AS 男员工数量, SUM(CASE WHEN `性别` = 'Female' THEN 1 ELSE 0 END) AS 女员工数量, SUM(CASE WHEN `婚姻状况` = 'Married' THEN 1 ELSE 0 END) AS 结婚员工数量, SUM(CASE WHEN `婚姻状况` = 'Single' THEN 1 ELSE 0 END) AS 未婚员工数量, SUM(CASE WHEN `教育程度` = 1 THEN 1 ELSE 0 END) AS 教育程度1人数, SUM(CASE WHEN `教育程度` = 2 THEN 1 ELSE 0 END) AS 教育程度2人数, SUM(CASE WHEN `教育程度` = 3 THEN 1 ELSE 0 END) AS 教育程度3人数, SUM(CASE WHEN `教育程度` = 4 THEN 1 ELSE 0 END) AS 教育程度4人数, SUM(CASE WHEN `教育程度` = 5 THEN 1 ELSE 0 END) AS 教育程度5人数, MAX(`通勤距离`) AS 最远通勤距离, MIN(`通勤距离`) AS 最近通勤距离, AVG(`总工作年限`) AS 平均总工作年限, AVG(`在本公司工作年限`) AS 平均本公司工作年限 FROM employees WHERE `在本公司工作年限` >= 2 GROUP BY `年龄组` ORDER BY `年龄组` ASC;

